import { useState, useEffect } from "react";
// import { Data } from "./emogiJson";

export const Main = () => {
  const url = " https://emoji.ymatuhin.workers.dev/";
  const [data, setData] = useState([]);
  const [valueFinder, setValueFinder] = useState("");

  //Прослушивание события из input
  const handlChange = (event) => setValueFinder(event.target.valueFinder);
//   console.log(handlChange);
  console.log(valueFinder.split(" "));
    // valueFinder.split(" ").forEach( elem => {.includes(elem)})
  const filterSmile = data.filter((elem) =>
    elem.keywords.split(" ")(toLowerCase().includes(valueFinder.toLowerCase())) 
  );


  const getData = async () => {
    let response = await fetch(url);
    let makeJson = await response.json();
    setData(makeJson);
  };
  useEffect(() => {
    getData();
  }, []);
  const displaySmile = filterSmile.map((elem) => (
    <div key={elem.keywords}>
      <div >{elem.title}</div>
      <div >{elem.symbol}</div>
      <div >{elem.keywords}</div>
    </div>
  ));
  return (
    <>
      <input type="text" onChange={handlChange} />

      <div>{displaySmile}</div>
    </>
  );
};
