export function Pagination({
  datalength,
  countCurrentPage,
  paginate,
  nextPage,
  lastPage,
}) {


const closetoStart = countCurrentPage < 3;
const closetoFinish = countCurrentPage > datalength - 1;  
console.log(closetoFinish);

// const firstNamber
// const lastNamber 



  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(datalength / countCurrentPage); i++) {
    pageNumbers.push(i);
  }
  const plenty = pageNumbers.map((elem) => elem);

  return (
    <div className="pagination ">
      <button className="btn btn-primary" onClick={lastPage}>
        Prev
      </button>
      {pageNumbers.map((number) => (
        <div>
          <div className="page-item">
            <a className="page-link" href="#" onClick={() => paginate(number)}>
              {number}
            </a>
          </div>
        </div>
      ))}
      <button className="btn btn-primary" onClick={nextPage}>
        Next
      </button>
    </div>
  );
}
