import { useState, useEffect } from "react";
// import { Data } from "./emogiJson";

export const Main = () => {
  const url = " https://emoji.ymatuhin.workers.dev/";
  const [data, setData] = useState([]);
  const [value, setValue] = useState("");

  //Прослушивание события из input
  const handlChange = (event) => setValue(event.target.value);
  //   console.log(handlChange);
  console.log(value.split(" "));
  // value.split(" ").forEach( elem => {displaySmile.includes(elem)})
  const filterSmile = data.filter((elem) =>
    elem.keywords.toLowerCase().includes(value.toLowerCase())
  );
  console.log(filterSmile);

  const getData = async () => {
    let response = await fetch(url);
    let makeJson = await response.json();
    setData(makeJson);
  };
  useEffect(() => {
    getData();
  }, []);
  const displaySmile = filterSmile.map((elem) => (
    <div id="box_cards" className="content" key={elem.keywords}>
      <div className="styleCard" className ="elem">

          <div className="smile">{elem.symbol}</div>
          <br />
          <div className="title">{elem.title}</div>
          <br />
          <div className="keywords">{elem.keywords}</div>
        </div>
      </div>

  ));
  return (
    <>
      <input
        className="input"
        placeholder="Placeholder"
        type="text"
        onChange={handlChange}
      />

      <div>{displaySmile}</div>
    </>
  );
};
